import { Component, OnInit, inject } from '@angular/core';
import { WeatherService } from './services/weather.service';
import { HttpClient } from '@angular/common/http';
import { environment } from 'src/environment/enviromment';
import { WeatherData, Product } from './models/data.model'

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent implements OnInit{
// export class AppComponent {

  http = inject(HttpClient);
  apiData : WeatherData[] = [];
  productsAppCom : Product[]= [];


  ngOnInit(): void {
    this.http.get<Product[]>('https://api.escuelajs.co/api/v1/products')
    .subscribe((data) =>{
      this.productsAppCom = data;
    })
  }

  // constructor(private weatherService: WeatherService){

  // }

  // ngOnInit(): void {
  //   // this.weatherService.getWeatherData()
  //   this.http.get<WeatherData[]>(environment.weatherApiBaseUrl)
  //   .subscribe((data) => {
  //     this.apiData = data;
  //   })
  // }
}
<div class="container">

  <div class="upper-data">

    <img src="../assets/pexels-imad-clicks-10679350.jpg" *ngIf="14 > 15" alt="">
    <img src="../assets/pexels-andreas-13722038.jpg" *ngIf="14 < 15"  alt="">

    <div class="weather-data">
      <div class="location"> CITY </div>
      <div class="temperature">{{ 14.40 | number: '1.0-0' }}°</div>
    </div>

  </div>

  <div class="lower-data">

    <div class="more-info-label">
      More info
    </div>

    <app-data  [dataWeather]="Wdata" *ngFor="let Wdata of apiData" ></app-data>

  </div>
</div>

<div class="container">
  <app-product [dataProduct]="product"  *ngFor="let product of productsAppCom" ></app-product>
</div>

<router-outlet></router-outlet>
